INSERT INTO `fugzp_admintools_waftemplates` (`admintools_waftemplate_id`, `reason`, `language`, `subject`, `template`, `enabled`, `email_num`, `email_numfreq`, `email_freq`, `created_on`, `created_by`, `modified_on`, `modified_by`) VALUES
(1, 'all', '*', 'Security exception on [SITENAME]', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\r\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\r\n<p>Hello,</p>\r\n<p>We would like to notify you that a security exception was detected on your site, [SITENAME], with the following details:</p>\r\n<p>IP Address: [IP] (IP Lookup: [LOOKUP])<br />Reason: [REASON]</p>\r\n<p>If this kind of security exception repeats itself, please log in to your site\'s back-end and add this IP address to your Admin Tools\'s Web Application Firewall feature in order to completely block the misbehaving user.</p>\r\n<p>Best regards,</p>\r\n<p>The [SITENAME] team</p>\r\n</div>\r\n<p style=\"font-size: x-small; color: #667;\">You are receiving this automatic email message because you are an administrator in <em>[SITENAME]</em>. <span style=\"line-height: 1.3em;\">Do not reply to this email, it\'s sent from an unmonitored email address.</span></p>\r\n</div>', 1, 5, 1, 'hour', '2014-04-14 14:36:34', 800, '2014-04-14 14:42:38', 800),
(2, 'user-reactivate', '*', 'User account temporarily blocked on [SITENAME]', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\r\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\r\n<p>Hello,</p>\r\n<p>We have detected several failed attempts to log in with user account [USER] on our site [SITENAME]. As a result, our system has automatically put a temporary block on the user account on [DATE].</p>\r\n<p>If you believe that you are getting this message in error, i.e. it was you who accidentally tried logging in with the wrong password, please click on the following link to lift the temporary block from your user account again:<br /><a href=\"[ACTIVATE]\">[ACTIVATE]</a></p>\r\n<p>Best regards,</p>\r\n<p>The [SITENAME] team</p>\r\n</div>\r\n<p style=\"font-size: x-small; color: #667;\">You are receiving this automatic email message because you have a user account in <em>[SITENAME]</em>. <span style=\"line-height: 1.3em;\">Do not reply to this email, it\'s sent from an unmonitored email address.</span></p>\r\n</div>', 1, 0, 0, '', '2014-04-24 14:44:49', 800, NULL, 0),
(3, 'adminloginfail', '*', 'Failed administrator login for user [USER] on [SITENAME]', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\r\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\r\n<p>Hello,</p>\r\n<p>We would like to notify you that user <strong>[USER]</strong> did <strong>not</strong> log in successfully to the administrator back-end area of your site, [SITENAME]. <span style=\"font-size: 12.1599998474121px; line-height: 15.8079996109009px;\">Further information:</span></p>\r\n<p style=\"font-size: 12.1599998474121px; line-height: 15.8079996109009px;\">Username: [USER]<br />IP address: [IP] ([LOOKUP])<br />Browser User Agent string: [UA]</p>\r\n<p style=\"font-size: 12.1599998474121px; line-height: 15.8079996109009px;\">* Country and continent information availability and accuracy depend on the GeoIP database installed on your site.</p>\r\n<p>If this is expected you do not need to do anything about it. If you suspect a hacking attempt, please log in to your site\'s back-end immediately and activate Admin Tools\' Emergency Off-Line mode at once.</p>\r\n<p>Best regards,</p>\r\n<p>The [SITENAME] team</p>\r\n</div>\r\n<p style=\"font-size: x-small; color: #667;\">You are receiving this automatic email message because you are an administrator in <em>[SITENAME]</em>. <span style=\"line-height: 1.3em;\">Do not reply to this email, it\'s sent from an unmonitored email address.</span></p>', 1, 5, 1, 'hour', '2015-07-27 13:52:45', 800, '2015-07-27 13:58:31', 800),
(4, 'adminloginsuccess', '*', 'User [USER] logged in on [SITENAME] administrator area', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\r\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\r\n<p style=\"font-size: 12.1599998474121px; line-height: 15.8079996109009px;\">Hello,</p>\r\n<p>We would like to notify you that user [USER] has just logged in to the administrator back-end area of your site, [SITENAME]. Further information:</p>\r\n<p>Username: [USER]<br />IP address: [IP] ([LOOKUP])<br />Browser User Agent string: [UA]</p>\r\n<p>* Country and continent information availability and accuracy depend on the GeoIP database installed on your site.</p>\r\n<p>If this is expected you do not need to do anything about it. If you suspect a hacking attempt, please log in to your site\'s back-end immediately and activate Admin Tools\' Emergency Off-Line mode at once.</p>\r\n<p style=\"font-size: 12.1599998474121px; line-height: 15.8079996109009px;\">Best regards,</p>\r\n<p style=\"font-size: 12.1599998474121px; line-height: 15.8079996109009px;\">The [SITENAME] team</p>\r\n</div>\r\n<p style=\"font-size: x-small; color: #667;\">You are receiving this automatic email message because you are an administrator in <em>[SITENAME]</em>. <span style=\"line-height: 1.3em;\">Do not reply to this email, it\'s sent from an unmonitored email address.</span></p>', 1, 5, 1, 'hour', '2015-07-27 13:58:08', 800, NULL, 0),
(5, 'ipautoban', '*', 'Automatic IP blocking notification for [IP] on [SITENAME]', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\r\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\r\n<p style=\"font-size: 12.1599998474121px; line-height: 15.8079996109009px;\">Hello,</p>\r\n<p>We would like to notify you that the IP address [IP] is now blocked from accessing your site, [SITENAME]<span style=\"font-size: 12.1599998474121px; line-height: 15.8079996109009px;\">, with the following details:</span></p>\r\n<p style=\"font-size: 12.1599998474121px; line-height: 15.8079996109009px;\">IP Address: [IP] (IP Lookup: [LOOKUP])<br />Reason: [REASON]<br />Banned until: [UNTIL]</p>\r\n<p>If this is your own IP address and you can no longer access your site please <a href=\"http://akee.ba/lockedout\">follow our instructions</a> to temporarily disable Admin Tools\' Web Application Firewall and clear the automatic IP ban.</p>\r\n<p style=\"font-size: 12.1599998474121px; line-height: 15.8079996109009px;\">Best regards,</p>\r\n<p style=\"font-size: 12.1599998474121px; line-height: 15.8079996109009px;\">The [SITENAME] team</p>\r\n</div>\r\n<p style=\"font-size: x-small; color: #667;\">You are receiving this automatic email message because you are an administrator in <em>[SITENAME]</em>. <span style=\"line-height: 1.3em;\">Do not reply to this email, it\'s sent from an unmonitored email address.</span></p>', 1, 5, 1, 'hour', '2015-07-27 14:20:00', 800, NULL, 0),
(6, 'configmonitor', '*', 'Configuration options for [AREA] modified on [SITENAME]', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\n<p>Hello,</p>\n<p>We would like to notify you that the configuration options for [AREA] on your site, [SITENAME], have been modified by user [USER]. More details about the origin of this change as follows:</p>\n<p>IP Address: [IP] (IP Lookup: [LOOKUP])<br />User Agent: [UA]</p>\n<p>If this change was not made by you or an administrator user you trust please review your site immediately as this would be an indication of hacking activity.</p>\n<p>Best regards,</p>\n<p>The [SITENAME] team</p>\n</div>\n</div>', 1, 0, 0, '', '2016-12-15 10:46:00', 800, '2016-12-15 10:46:00', 800),
(7, 'criticalfiles', '*', 'Critical file modified on [SITENAME]', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\n<p>Hello,</p>\n<p>We would like to notify you that one or more critical files have been modified on your site, [SITENAME]. The list of files modified on your site is as follows:</p>\n[INFO]\n<h3>Should I be worried?</h3>\n<p>Critical files, in the context of this email, are the files most usually targeted by hackers upon successful hacking of a site. These files can also be modified for legitimate reasons, for example when you save your site\'s Global Configuration or when you update Joomla! or one of its templates.</p>\n<p>You should NOT worry if you received this email after you, or another administrator you trust, performed any of the following changes on your site:</p>\n<ul>\n	<li>Restored the site from a backup</li>\n	<li>Modified the Global Configuration</li>\n	<li>Updated Joomla!</li>\n	<li>Updated a site template</li>\n</ul>\n<p>If this message was not sent to you as the result of such a desirable and expected change please review your site immediately as this would be an indication of hacking activity.</p>\n<p>Best regards,</p>\n<p>The [SITENAME] team</p>\n</div>\n</div>', 1, 0, 0, '', '2017-04-05 13:15:00', 800, '2017-04-05 13:15:00', 800),
(8, 'superuserslist', '*', 'Super Users were added to [SITENAME]', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\n<p>Hello,</p>\n<p>\nWe would like to notify you that we detected the suspicious addition of one or more Super User accounts to your site, [SITENAME]. These new Super User accounts do not seem to have been created through the regular means, i.e. Joomla\'s &ldquo;Users&rdquo; page. Therefore they have been blocked. The new Super User accounts detected <strong>and blocked</strong> are:\n</p>\n[INFO]\n<p>\n<h3>Do I need to worry?</h3>\n<p>\nSuper User accounts have full access to your site. They can modify all content, without any restriction. They can also install software which can access or modify every aspect of your site, including arbitrary files, database content and configuration settings. Only grant Super User access to people you trust.\n</p>\n<p>\nIf you are receiving this email it means that these Super User accounts were created outside Joomla\'s &ldquo;Users&rdquo; page. Typically this means that a hacker found a way to bypass your site\'s or your server\'s security and tried to surreptitiously create Super User accounts for themselves. For this reason these accounts were blocked, i.e. they cannot be used to log into your site.\n</p>\n<p>\nIt is possible, however, that you do receive this email in error: if you or another Super User did intend to create these Super User accounts and used a third party tool to do that. If this is the case then do not worry. Just log in to your site\'s administrator backend with your own Super User account, go to the Users page, edit the users listed further above in this email and unblock them.\n</p>\n<p>\nIf you or another Super User did NOT intend to create these Super Users we recommend auditing your site and your server for any other signs of anomalous activity.\n</p>\n<p>Best regards,</p>\n<p>The [SITENAME] team</p>\n</div>\n</div>', 1, 0, 0, '', '2017-04-06 09:50:00', 800, '2017-04-06 09:50:00', 800),
(9, 'rescueurl', '*', 'Rescue URL requested on [SITENAME] for [USER]', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\n<p>Hello,</p>\n<p>We would like to notify you that user [USER] has requested a Rescue URL for your site, [SITENAME]. If you made that request yourself please click on the following link, or copy and paste the URL to your browser. This will let you log in to the administrator backend of your site where you can modify unblock yourself and / or change Admin Tools\' configuration. The Rescue URL is:</p>\n<p><a href=\"[RESCUEURL]\" rel=\"alternate\">[RESCUEURL]</a></p>\n<p>The information towards the end of this email lets you know what to do once you are logged into your site\'s backend.</p>\n<h3>I have no idea why I\'m receiving this email</h3>\n<p>Look under \"What is the Rescue URL\" further down. You <strong>DO NOT</strong> need to take any action and you <strong>DO NOT</strong> need to worry.</p>\n<h3>Who requested the Rescue URL?</h3>\n<p>Username: [USER]<br />IP Address: [IP] ([LOOKUP])<br />User Agent: [UA]</p>\n<p></p>\n<h3>What is the Rescue URL?</h3>\n<p>Sometimes an overzealous Admin Tools configuration can result in accidentally blocking you, a Super User from the site. Normally that would require you to rename the file of Admin Tools\' system plugin to unblock yourself. This is a bit complicated and susceptible to errors.</p>\n<p>The Rescue URL feature works around that. By accessing a special URL when you are blocked out of the site youare sent this email. The URL in this email is only valid forthe same browser and IP address that made the request and only for a limited amount of time. Accessing it will get you to a <em>temporarily</em> unblocked administrator login page.This lets you access the administrator backend of your site to unblock yourself and / or modify Admin Tools settings.</p>\n<p>By default, this feature is enabledon all installations of Admin Tools. Moreover, a message about accessing the Rescue URL is displayed by default when someone gets blocked UNLESS you set upa \"Custom message\" in the Configure WAF page of Admin Tools. If you have no idea why you received this message it\'s probably because someone saw the default IP blocked message and guessed your Super User email correctly. Don\'t worry though, what they did is completely useless since they would also need access to your email account and your Super User password to access your site, i.e. they would need to have already hacked you.As it\'s already documented, we strongly recommend that you change the \"Custom message\" to make it less obvious that your site is protected by Admin Tools. If you do not wish to use the Rescue URL feature -and are OK manually renaming files per the documentation when you get blocked- go to Extensions, Plugins and edit the options of the Admin Tools system plugin. You\'ll see an option for Rescue URL. Set it to No to disable this feature.</p>\n<h3>What to do after logging in?</h3>\n<p>If your IP is blocked by Admin Tools go toComponents, Admin Tools and click the big Unblock My IP button at the top of the page.</p>\n<p>If you keep getting your IP blocked without you doing anything suspicious to the best of your knowledge,the IP blocked is always the same but different than the one reported by<a href=\"http://www.ip-adress.eu/\">http://www.ip-adress.eu/ </a><em>and</em><a href=\"https://www.whatismyip.com/\">https://www.whatismyip.com/</a>then your site might be behind a CDN or a reverse proxy. In this case, additionally to the instructions above, please go to Components, Admin Tools, Web Application Firewall, Configure WAF, click on the Basic Protection Features tab. Set the \"Enable IP workarounds\" option to Yes - no matter what the automatically detectedrecommendation is.</p>\n<p>If, however, your real IP address as reported by the two tool listed above keeps getting banned you should troubleshoot the reason of the ban.Go to Components, Admin Tools, Web Application Firewall, Security Exceptions Log and check the Reason and Target URL for the entries which have your IP address in the IP address field. If you are not sure what that means, please <a href=\"https://www.akeeba.com/support/admin-tools.html\" rel=\"alternate\">file a support ticket</a>remembering to copy the information from the Security Exceptions Log. Kindly note that you need to have an active subscription to receive support.</p>\n<p>If you have forgotten your Administrator Secret URL parameter go to Components, Admin Tools, Web Application Firewall, Configure WAF, click on the Basic Protection Features tab andfind the \"Administrator secret URL parameter\" option. Change or remove all of the text in that box to reset or unset, respectively, this feature.</p>\n<h3>What to do if I still cannot log in to my site?</h3>\n<p>There are two cases where the Rescue URL feature, or renaming the Admin Tools system plugin\'s file, will not help you. These are the two cases where Admin Tools has created a <em>server</em> configuration file, meaning that you are blocked by <em>your server</em>, not Admin Tools.</p>\n<p>The first is the <strong>Administrator password protection</strong> feature. Please delete the files named <code>.htaccess</code> and <code>.htpasswd</code> from your site\'s <code>administrator</code> directory.</p>\n<p>The other case is when you\'ve used the <strong>.htaccess Maker</strong> feature of Admin Tools. In this case there\'s a <code>.htaccess</code>file in your site\'s root. You may want to replace its contents with the <a href=\"https://raw.githubusercontent.com/joomla/joomla-cms/staging/htaccess.txt\" rel=\"alternate\">default Joomla! .htaccess file content</a>.</p>\n<p>In both cases you should not that thefiles have names beginning with a dot. That makes them <em>hidden</em>. You will need to enable the display of hidden files to edit / delete those files. If you are unsure how to do that please ask your host and tell them that you need to edit/delete <em>hidden</em> files. Usually they will point out an option in their hosting control panel\'s file manager.</p>\n<p>In any other case your issue is unrelated to Admin Tools. Do you have another security plugin on your site? If you do, check its settings. If not, check with your host.More often than not hostshave their own server security systems which can block you out of your site.</p>\n</div>\n</div>', 1, 1, 1, 'minute', '2017-06-20 09:50:00', 800, '2017-06-20 09:50:00', 800),
(10, 'criticalfiles_global', '*', 'Critical file modified on [SITENAME]', '<div style=\"background-color: #e0e0e0; padding: 10px 20px;\">\n<div style=\"background-color: #f9f9f9; border-radius: 10px; padding: 5px 10px;\">\n<p>Hello,</p>\n<p>We would like to notify you that one or more critical files that you decided to monitor have been modified on your site, [SITENAME]. The list of files modified on your site is as follows:</p>\n[INFO]\n<p>If this message was not sent to you as the result of such a desirable and expected change please review your site immediately as this would be an indication of hacking activity.</p>\n<p>Best regards,</p>\n<p>The [SITENAME] team</p>\n</div>\n</div>', 1, 0, 0, '', '2017-04-05 13:15:00', 800, '2017-04-05 13:15:00', 800);
