(24, '1', 'Mission Statement'),
(25, '1', 'Create a knowledge-based society by transcending the language barriers and providing content and services to citizens, in their own language, both in the form of speech and text.'),
(27, '1', '{\"principles0\":{\"title\":\"Create an Ecosystem\",\"text\":\"Create and nurture an ecosystem involving government, industry, academia, research groups, start-ups and individuals\"},\"principles1\":{\"title\":\"Open Source Datasets\",\"text\":\"Create large open source datasets and models by bringing all contributions, both institutional and citizen, into a shared repository that nurtures innovation\"},\"principles2\":{\"title\":\"Contribute and Leverage\",\"text\":\"Encourage the ecosystem to develop innovative products and services in Indian languages by leveraging the open repository of datasets and models.\"}}'),
